mapper.select:
select status
from post
where post_id = #{postId};

select

from post


        and status > 0


        or title likes CONCAT('%', #{postCondition.title}, '%')


        or description likes CONCAT('%', #{postCondition.description}, '%')


        and authority = #{postCondition.authority}


        and post_category = #{postCondition.postCategory}


        and user_id = #{postCondition.userId}


        and status = #{postCondition.status}


        and status > 0


limit #{rowIndex}, #{pageSize}

select
  count(post_id)
from post


        and status > 0


        or title likes CONCAT('%', #{postCondition.title}, '%')


        or description likes CONCAT('%', #{postCondition.description}, '%')


        and authority = #{postCondition.authority}


        and post_category = #{postCondition.postCategory}


        and user_id = #{postCondition.userId}


        and status = #{postCondition.status}


        and status > 0

mapper.update:
UPDATE post
post_id = #{postId,jdbcType=INTEGER}, create_time = #{createTime,jdbcType=TIMESTAMP}, last_edit_time = #{lastEditTime,jdbcType=TIMESTAMP}, status = #{status,jdbcType=INTEGER}, parent_id = #{parentId,jdbcType=INTEGER}, page_view = #{pageView,jdbcType=INTEGER}, authority = #{authority,jdbcType=INTEGER}, user_id = #{userId,jdbcType=INTEGER}, post_category = #{postCategory,jdbcType=INTEGER}, title = #{title,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, browse_id = #{browseId,jdbcType=INTEGER}
where post_id = #{postId,jdbcType=INTEGER}
